sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	if (current.state=="Processing"){
		try {
			if (current.topic == "Add CatItem to CRSDB_CatItem"){
				var body = JSON.parse(current.body);
				var catalogID = gs.getProperty('x_880077_crossplan.main_category_title');
				var provider_name = "";
				var apiversion = "";
				var CRD_item = body;	
				var check = false;
				if (CRD_item["metadata"]["ownerReferences"]){
					for (j in CRD_item["metadata"]["ownerReferences"]){
						var CRD_item_ref = CRD_item["metadata"]["ownerReferences"][j];
						if (CRD_item_ref["kind"] == "Provider"){				
							check = true;
							provider_name = CRD_item_ref["name"];	
							apiversion = CRD_item_ref["apiVersion"];	
							break;
						}			
					}		
				}
				if(check == true){
					var providerID = "" + CRSDB_Category.getIDByName(provider_name);
					//var catItem = {};

					var catItem = new GlideRecord("x_880077_crossplan_crsdb_catitem");
					catItem.initialize();

					catItem.name = CRD_item["metadata"]["name"];
					catItem.type = "CRD_CatItem";
					catItem.group = CRD_item["spec"]["group"];
					catItem.short_description = apiversion;
					catItem.status = "Waiting For Create";
					catItem.catalogs = catalogID;
					catItem.category = "" + providerID;
					catItem.type = "provider";

					//CRSDB_CatItem.write(catItem);					
					catItem.insert();						


					current.state = "Completed";
				}
				else{
					current.state = "Not a Provider";
				}
				/*
				else {
					var catItem2 = {};
					catItem2.name = CRD_item["metadata"]["name"];
					catItem2.type = "CRD_CatItem";
					catItem2.group = CRD_item["spec"]["group"];
					catItem2.short_description = apiversion;
					catItem2.status = "Waiting For Create";
					catItem2.catalogs = catalogID;
					catItem2.category = "" + providerID;
					catItem2.type = "provider";
					CRSLog.debug("Can not Find Provider " + JSON.stringify(catItem2));
				}

				if(catItem.category == "-1"){
					CRSLog.debug(current.body);
					//CRSLog.debug(JSON.stringify(current.body));
				}
				*/

			}
		}
		catch(err) {
			CRSLog.debug("Error Business Rule" + err);
			if (current.retry >= gs.getProperty("x_880077_crossplan.JobRetryCount")){
				current.state = "Error";
			}
			else{
				current.state = "Ready";
				current.retry += 1; 
			}		
		}
		current.state == "Completed";
		current.update();
	}

})(current, previous);
