sys_script_include.script:
var Category_Script = Class.create();
Category_Script.prototype = {
    initialize: function() {
    },

    type: 'Category_Script'
};

var Init_Crossplane_Title = "CrossplaneV2";
var Init_Provider_Title = "CRS_Provider";

var service_CatalogID = "e0d08b13c3330100c8b837659bba8fb4"; //Service Catalog
var CRS_CatalogID = "c3e2848947be59104b6fcc88f36d432b"; //CrossplaneV2 Catalog

var scope = "bdc0bd6847ba19104b6fcc88f36d43bd"; //CrossplaneV2 App
var sc_catalog = "";
Category_Script.InitCatalog = function create() {	
	sc_catalog = CRS_CatalogID;
	var provider_CategoryID = Category_Script.create(Init_Provider_Title,"Provider DESC");
	CRS_Log.debug("Provider Category ID :" + provider_CategoryID);

	var AWS_CategoryID = Category_Script.create("AWS",provider_CategoryID,"AWS DESC");
	CRS_Log.debug("AWS Category ID :" + AWS_CategoryID);

	return provider_CategoryID;
};


Category_Script.InitCategory = function create() {	
	sc_catalog = service_CatalogID;
	var crossplaneV2_CategoryID = Category_Script.create(Init_Crossplane_Title,"CRS V2 Desc");
	CRS_Log.debug("CRS Category ID :" + crossplaneV2_CategoryID);

	var provider_CategoryID = Category_Script.createWithParent(Init_Provider_Title,"Provider Desc",crossplaneV2_CategoryID);
	CRS_Log.debug("Provider Category ID :" + provider_CategoryID);

	var AWS_CategoryID = Category_Script.createWithParent("AWS","AWS Desc",provider_CategoryID);
	CRS_Log.debug("AWS Category ID :" + AWS_CategoryID);

	return provider_CategoryID;
};


Category_Script.createWithParent = function create(title,desc,parentID) {
	sc_catalog = service_CatalogID;
    var category = new sn_sc.CatCategory();	
	category.setAttributes({
		"title" : title, 
		"sc_catalog" : sc_catalog, 
		"sys_scope": scope,
		"parent": parentID,
		"description": desc
	});	
	var categoryID = category.create();
	CRSDB_Category.write(title,categoryID);	
	return categoryID;
};

Category_Script.create = function create(title,desc) {
	sc_catalog = service_CatalogID;
    var category = new sn_sc.CatCategory();	
	category.setAttributes({
		"title" : title, 
		"sc_catalog" : sc_catalog, 
		"sys_scope": scope,
		"description": desc
	});
	var categoryID = category.create();
	CRSDB_Category.write(title,categoryID);	
	return categoryID;
};
