sys_script_include.script:
var Category_Script = Class.create();
Category_Script.prototype = {
    initialize: function() {
    },

    type: 'Category_Script'
};

Category_Script.InitCatalog = function create() {	
	sc_catalog = CRS_CatalogID;
	CRSDB.deleteByName(Init_Provider_Title);
	var provider_CategoryID = Category_Script.create(Init_Provider_Title,"Provider DESC");
	CRSDB.write(Init_Provider_Title,provider_CategoryID);
	CRSLog.debug("Provider Category ID :" + provider_CategoryID);
	/*
	var AWS_CategoryID = Category_Script.create("AWS",provider_CategoryID,"AWS DESC");
	CRSLog.debug("AWS Category ID :" + AWS_CategoryID);
*/
};   

Category_Script.InitCategoryOld = function create() {	
	CRSDB_Category.deleteByType("Category Init");
	var category_title = CRSDB.getValue("category_title");
	var provider_title = CRSDB.getValue("provider_Title");
	var scope = CRSDB.getValue("scope");
	var catalogID = CRSDB.getValue("CatalogID");
	CRSLog.debug("category_title" + category_title + " provider_title" + provider_title + " scope" + scope + " ssssss" + catalogID + " catalogID" + catalogID);
	var crossplaneV2_CategoryID = Category_Script.create(scope,catalogID,category_title,"CRS V2 Desc","Category Init");
	var provider_CategoryID = Category_Script.createWithParent(scope,catalogID,provider_title,"Provider Desc","Category Init",crossplaneV2_CategoryID);
	CRSLog.debug("Category Init Completed");
};

Category_Script.write_InitCategory = function create() {		
	var catalogID = gs.getProperty('x_880077_crossplan.serviceCatalogID');

	var category = {};
	category.name = gs.getProperty('x_880077_crossplan.main_category_title');
	category.type = "Init";
	category.status = "Waiting For Create";
	category.catalog = catalogID;
	CRSDB_Category.write(category);

	var provider = {};
	provider.name = gs.getProperty('x_880077_crossplan.main_provider_title');
	provider.type = "Init";
	provider.status = "Waiting For Create";
	provider.catalog = catalogID;
	CRSDB_Category.write(provider);

	CRSLog.debug("Write Category Init Completed");
};





Category_Script.createWithParent = function create(scope,catalogID,name,desc,type,parentID) {
    var category = new sn_sc.CatCategory();	
	category.setAttributes({
		"title" : name, 
		"sc_catalog" : catalogID, 
		"sys_scope": scope,
		"parent": parentID,
		"description": desc
	});	
	var categoryID = category.create();	
	CRSDB_Category.write(name,categoryID,type);
	return categoryID;
};

Category_Script.create = function create(current) {
    var category = new sn_sc.CatCategory();	
	category.setAttributes({
		"title" : current.name, 
		"sc_catalog" : current.catalog, 
		"sys_scope": current.scope,
		"description": current.description
	});
	current.categoryID = category.create();	
	return current.categoryID;
};

Category_Script.create_providers = function create_providers(){
	var scope = CRSDB.getValue("scope");
	var catalogID = CRSDB.getValue("CatalogID");
	var providerID = CRSDB_Category.getID(CRSDB.getValue("provider_Title"));
	var Token = CRSDB.getValue("Token");	
	var baseurl = CRSDB.getValue("baseurl");
	var provider_endpoint = CRSDB.getValue("provider_endpoint");
	var url = baseurl + provider_endpoint;
	var response = CRS_API.requestGetJson(url,Token);	

	providers = response["items"];
	CRSDB_Category.deleteByType("provider");
	for (i in providers){
		provider = providers[i];
		Category_Script.createWithParent(scope,catalogID,provider["metadata"]["name"],provider["spec"]["package"],"provider",providerID);
	}
};

Category_Script.write_provider_category_toDB = function write_provider_category_toDB(){
	CRSLog.debug("Run -> write_provider_category_toDB");
	var catalogID = gs.getProperty('x_880077_crossplan.serviceCatalogID');
	var providerID = CRSDB_Category.getID(gs.getProperty('x_880077_crossplan.main_provider_title'));
	var Token = gs.getProperty('x_880077_crossplan.Tokens');	
	var baseurl = gs.getProperty('x_880077_crossplan.base_urls');
	var provider_endpoint = gs.getProperty('x_880077_crossplan.provider_endpoint');
	var url = baseurl + provider_endpoint;
	var response = CRS_API.requestGetJson(url,Token);		
	providers = response["items"];
	for (i in providers){
		var current = {};
		provider = providers[i];
		current.name = provider["metadata"]["name"];
		current.type = "Provider";
		current.status = "Waiting For Create";
		current.catalog = catalogID;
		current.description = provider["spec"]["package"];
		current.parent = providerID;
		CRSDB_Category.write(current);		
	}
	CRSLog.debug("Completed -> write_provider_category_toDB");
};

Category_Script.create_waiting_provider_category = function create_waiting_provider_category(){
	CRSLog.debug("Run -> create_waiting_provider_category");
	var scope = gs.getProperty('x_880077_crossplan.scope_id');	
	gr = CRSDB_Category.getByFilter("status","Waiting For Create");
	while(gr.next()){
		var current = gr;
		CRSLog.debug("fff"+current.scope);
		current.scope = scope;
		//CRSLog.debug("created: " + Category_Script.create(current));
		//gr.update();
		//return gr;
	}	

	CRSLog.debug("Completed -> create_waiting_provider_category");
};
