sys_script_include.script:
var CRS_Log = Class.create();
CRS_Log.prototype = {
    initialize: function() {
    },

    type: 'CRS_Log'
};

CRS_Log.logHeader = "CRS_Log";
var logVerbosity = 3;
CRS_Log.logLevel = ((logVerbosity!= '' && !gs.nil(logVerbosity) && logVerbosity != undefined) && (logVerbosity >= 0 && logVerbosity <= 3))  ? logVerbosity : 1;


CRS_Log.debug = function(s) {
		gs.info( 'DEBUG >> '+s,CRS_Log.logHeader );		
};

CRS_Log.info =  function(s) {
	if (CRS_Log.logLevel >= 2){
		gs.info( s,CRS_Log.logHeader );
	}
};

CRS_Log.warn = function(s) {
	if (CRS_Log.logLevel >= 1){
		gs.warn( s,CRS_Log.logHeader );
	}
};

CRS_Log.error = function(s) {
	if (CRS_Log.logLevel >= 0){
		gs.error( s,CRS_Log.logHeader );
	}
};
