sys_script_include.script:
var CRSDB = Class.create();
CRSDB.prototype = {
    initialize: function() {
    },

    type: 'CRSDB'
};

var IDTable = "x_880077_crossplan_db"; //DB Table

CRSDB.write = function write(name,id) {	
	var old_data = CRSDB.find(name);
	if ( old_data != -1){
		old_data.deleteRecord();
	}
	var gr = new GlideRecord(IDTable);
	gr.id= id;
	gr.name =  name;		
	gr.insert();				
};


CRSDB.find = function find(name){
	var gr = new GlideRecord(IDTable);
	gr.query();
	while(gr.next()){
		if( gr.name == name){
			CRS_Log.debug("Find: " + gr.name);
			return gr.id;
		}		
	}
	return -1;
};

CRSDB.deleteAll = function find(IDTable){
	var gr = new GlideRecord(IDTable);
	gr.query();
	while(gr.next()){
		var catalogItem = new sn_sc.CatItem(gr.id);
		catalogItem.deleteRecord(true);
		gr.deleteRecord();
	}

	gr.query();
	while(gr.next()){
		if( gr.name == name){
			CRS_Log.debug("Find: " + gr.name);
			return gr;
		}		
	}
	return -1;
};
