sys_script_include.script:
var CRSDB_Category = Class.create();
CRSDB_Category.prototype = {
    initialize: function() {
    },

    type: 'CRSDB_Category'
};


var IDTable = "x_880077_crossplan_crsdb_categorylist"; //DB Table

CRSDB_Category.write = function write(name,id) {	
	var gr = new GlideRecord(IDTable);
	gr.id= id;
	gr.name =  name;		
	gr.insert();				
};


CRSDB_Category.find = function find(name){
	var gr = new GlideRecord(IDTable);
	gr.query();
	while(gr.next()){
		if( gr.name == name){
			CRS_Log.debug("CRSDB_CatItem_List Found: " + gr.name);
			return gr;
		}		
	}
	return -1;
};

CRSDB_Category.deleteAll = function deleteAll(){
	var gr = new GlideRecord(IDTable);
	gr.query();
	while(gr.next()){
		var category = new sn_sc.CatCategory(gr.id);
		category.deleteRecord(true);
		gr.deleteRecord();
	}		
};
