sys_script_include.script:
var CRSDB_MainCategory = Class.create();
CRSDB_MainCategory.prototype = {
    initialize: function() {},

    type: 'CRSDB_MainCategory'
};

CRSDB_MainCategory.write = function() {
    var mainProviderTitle = gs.getProperty('x_880077_crossplan.main_provider_title');
    var serviceCatalogID = gs.getProperty('x_880077_crossplan.serviceCatalogID');
    var attr = {
        "name": mainProviderTitle,
        "sc_catalog": serviceCatalogID,
        "type": "main"
    };
    var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
    gr.initialize();
    gr.body = JSON.stringify(attr);
    gr.topic = 'Write Main Category';
    gr.state = "Ready";
    gr.insert();
};

CRSDB_MainCategory.create = function() {
    var category = new GlideRecord('x_880077_crossplan_crsdb_category');
	var mainCategoryTitle = gs.getProperty('x_880077_crossplan.main_provider_title');
    category.addQuery('status', 'Waiting For Create');
	category.addQuery('name', mainCategoryTitle);
    category.query();
    while (category.next()) {
        var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        gr.initialize();
        gr.topic = 'Create Main Category';
        gr.state = 'Ready';
        var attr = {
            "title": category.name.toString(),
            "sc_catalog": category.catalog.toString(),
            "type": category.type.toString(),
			"sys_id": category.sys_id.toString()
        };
        gr.body = JSON.stringify(attr);
        gr.insert();
    }
};
