sys_script_include.script:
var Catalog_Item_Script = Class.create();
Catalog_Item_Script.prototype = {
    initialize: function() {
    },

    type: 'Catalog_Item_Script'
};



Catalog_Item_Script.create = function createCatalogItem(scope,catalogID,name,desc,categoryId,type) {
    var catalogItem = new sn_sc.CatItem();
    catalogItem.setAttributes({
        "name": name,
        "short_description": desc,
		"price": 20,
		"sys_scope": scope,
		"sc_catalogs": catalogID,
		"category":categoryId 
    });	
    var catalogItemID = catalogItem.create();
	//CRSDB_CatItem.write(name,catalogItemID,type);
	return catalogItemID;	
};

Catalog_Item_Script.create_provider_DB = function create_provider_catItem(){
	CRSLog.debug("ID: " + CRSDB.getValue("CatalogID") + " SCOPE: " + CRSDB.getValue("sys_scope"));
	var Token = CRSDB.getValue("Token");	
	var baseurl = CRSDB.getValue("baseurl");
	var provider_endpoint = CRSDB.getValue("CRD_endpoint");
	var url = baseurl + provider_endpoint;
	var response = CRS_API.requestGetJson(url,Token);
	var CRD_items = response["items"];
	CRSDB_Provider.deleteAll();
	for (i in CRD_items){	
		var provider_name = "";
		var apiversion = "";
		var CRD_item = CRD_items[i];	
		for (j in CRD_item["metadata"]["ownerReferences"]){
			var CRD_item_ref = CRD_item["metadata"]["ownerReferences"][j];
			if (CRD_item_ref["kind"] == "Provider"){				
				provider_name = CRD_item_ref["name"];	
				apiversion = CRD_item_ref["apiVersion"];	
			}			
		}		
		CRSDB_Provider.write(CRD_item["metadata"]["name"],CRD_item["spec"]["group"],provider_name,apiversion,"-1","Waiting For Create CatItem");
	}
};
Catalog_Item_Script.create_provider_catItem = function create_provider_catItem(){
	var scope = CRSDB.getValue("scope");
	var catalogID = CRSDB.getValue("CatalogID");
	var gr = CRSDB_Provider.getDB();
	gr.query();
	while(gr.next()){
		if(gr.status == "Waiting For Create CatItem"){
			Catalog_Item_Script.create(scope,catalogID,gr.name,"description", CRSDB_Category.getID(gr.provider_name), "provider_catItem");
		}		
	}
	//CRSLog.debug("Item : " + CRD_item_metadata["name"] + "category: " + categoryID);
	//CRSDB_CatItem.deleteByName(CRD_item_metadata["name"]);										
	//Catalog_Item_Script.create(CRD_item_metadata["name"],"description", categoryID, "provider_catItem");
	CRSLog.debug("Finish");
};
