sys_script_include.script:
var Catalog_Item_Script = Class.create();
Catalog_Item_Script.prototype = {
    initialize: function() {
    },

    type: 'Catalog_Item_Script'
};

var scope = "bdc0bd6847ba19104b6fcc88f36d43bd"; //CrossplaneV2 App

Catalog_Item_Script.Init = function Init() {
	CRSDB_CatItem.deletAll(title,categoryID);
};

Catalog_Item_Script.create = function createCatalogItem(name,desc,catalogIds,categoryId, price) {
    var catalogItem = new sn_sc.CatItem();

    catalogItem.setAttributes({
        "name": name,
        "short_description": desc,
		"price":price,
		"sys_scope": scope,
		"sc_catalogs": catalogIds,
		"category":categoryId 
    });

    var catalogItemID = catalogItem.create(); // create new catalog item
	CRSDB_CatItem.write(name,catalogItemID);
	return catalogItemID;

};

Catalog_Item_Script.deleteByName = function createCatalogItem(name) {
	var gr = new GlideRecord("sc_cat_item");
	gr.query();
	while(gr.next()){
		if (gr.name == name){
				var catalogItem = new sn_sc.CatItem(gr.getUniqueValue());
				catalogItem.deleteRecord(true);
				gr.deleteRecord();
			}		
	}	
};
