sys_script_include.script:
var CRSDB_CatItem2 = Class.create();
CRSDB_CatItem2.prototype = {
    initialize: function() {},

    type: 'CRSDB_CatItem2'
};

CRSDB_CatItem2.write = function() {
    // 	var Token = gs.getProperty('x_880077_crossplan.Tokens');	
    // 	var baseurl = gs.getProperty('x_880077_crossplan.base_urls');
    // 	var endpoint = gs.getProperty('x_880077_crossplan.CRD_endpoint');
    // 	var url = baseurl + endpoint;
    // 	var response = CRS_API.requestGetJson(url,Token);
    var response = JSON.parse(gs.getProperty('x_880077_crossplan.json2'));
    var catItems = response["items"];

    for (var i in catItems) {
        var gr = new GlideRecord("x_880077_crossplan_crs_jobqueue");
        gr.initialize();
        gr.state = "Ready";
        gr.body = JSON.stringify(catItems[i]);
        gr.topic = "Write CatItem";
        gr.request = catItems[i]["metadata"]["name"];
        gr.insert();
    }
};

CRSDB_CatItem2.create = function() {
    var catItems = new GlideRecord("x_880077_crossplan_crsdb_catitem");
    catItems.addQuery('status', 'Waiting For Create');
    catItems.query();

    while (catItems.next()) {
        var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        gr.topic = "Create CatItem";
        var attributes = {
            "name": catItems.name.toString(),
            //"description": catItems.description.toString(),
            "parent": catItems.category.toString(),
            "status": catItems.status.toString(),
            //"type": catItems.type.toString(),
            "catalogs": catItems.catalogs.toString(),
            "sys_id": catItems.sys_id.toString()
        };
        gr.body = JSON.stringify(attributes);
        gr.state = "Ready";
        gr.insert();
    }
};
