sys_script_include.script:
var CRSDB_GroupCategory = Class.create();
CRSDB_GroupCategory.prototype = {
    initialize: function() {},

    type: 'CRSDB_GroupCategory'
};

CRSDB_GroupCategory.write = function() {
    var response = JSON.parse(gs.getProperty('x_880077_crossplan.json2'));
    var groups = response["items"];
    for (var i in groups) {
        var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        gr.initialize();
        gr.topic = "Write Group Category";
		var attr={
			"providerName":groups[i]["metadata"]["ownerReferences"][0]["name"],
			"groupName":groups[i]["spec"]["group"]
		};
		gr.body = JSON.stringify(attr);
		gr.state = "Ready";
		gr.insert();
    }
};
