sys_script_include.script:
var CRSDB_GroupCategory = Class.create();
CRSDB_GroupCategory.prototype = {
    initialize: function() {},

    type: 'CRSDB_GroupCategory'
};

CRSDB_GroupCategory.write = function() {
    var response = JSON.parse(gs.getProperty('x_880077_crossplan.json2'));
    var groups = response["items"];

    forloop: for (var i in groups) {
        var existCat = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        existCat.query();
        while (existCat.next()) {
            if (existCat.body.includes(groups[i]["spec"]["group"]))
                continue forloop;
        }
		CRSLog.warn('asdasd');
        var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        gr.initialize();
        gr.topic = "Write Group Category";
        var attr = {
            "providerName": groups[i]["metadata"]["ownerReferences"][1]["name"],
            "groupName": groups[i]["spec"]["group"]
        };
        gr.body = JSON.stringify(attr);
        gr.state = "Ready";
        gr.insert();
    }
};

CRSDB_GroupCategory.create = function() {
    var cat = new GlideRecord('x_880077_crossplan_crsdb_category');
    cat.addQuery('status', 'Waiting For Create');
    cat.query();
    while (cat.next()) {
        var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        gr.topic = "Create Group Category";
        var attributes = {
            "name": cat.name.toString(),
            "description": cat.description.toString(),
            "parent": cat.parent.toString(),
            "status": cat.status.toString(),
            "type": cat.type.toString(),
            "catalog": cat.catalog.toString(),
            "sys_id": cat.sys_id.toString()
        };
        gr.body = JSON.stringify(attributes);
        gr.state = "Ready";
        gr.insert();
    }
};
