sys_script_include.script:
var CRSDB_Provider = Class.create();
CRSDB_Provider.prototype = {
    initialize: function() {},

    type: 'CRSDB_Provider'
};

CRSDB_Provider.Write = function() {
    //script

    // var catalogID = gs.getProperty('x_880077_crossplan.serviceCatalogID');
    // var providerID = CRSDB_Category.getIDByName(gs.getProperty('x_880077_crossplan.main_provider_title'));
    // var Token = gs.getProperty('x_880077_crossplan.Tokens');
    // var baseurl = gs.getProperty('x_880077_crossplan.base_urls');
    // var endpoint = gs.getProperty('x_880077_crossplan.provider_endpoint');
    // var url = baseurl + endpoint;
    //var response = CRS_API.requestGetJson(url,Token);	

    var response = JSON.parse(gs.getProperty('x_880077_crossplan.json2'));
    var providers = response["items"];
    for (var i in providers) {
        var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        gr.topic = "Write Category";
        gr.body = JSON.stringify(providers[i]);
        gr.state = "Ready";
        gr.insert();
    }
};

CRSDB_Provider.Create = function() {
    var cat = new GlideRecord('x_880077_crossplan_crsdb_category');
    cat.addQuery('type', 'provider');
    cat.addQuery('status', 'Waiting For Create');
    cat.query();
    while (cat.next()) {
        var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
        gr.topic = "Create Category";
        var attributes = {
            "name": cat.name.toString(),
            "description": cat.description.toString(),
            "parent": cat.parent.toString(),
            "status": cat.status.toString(),
            "type": cat.type.toString(),
			"catalog" : cat.catalog.toString(),
            "sys_id": cat.sys_id.toString()
        };
        gr.body = JSON.stringify(attributes);
        gr.state = "Ready";
        gr.insert();
    }
};

CRSDB_Provider.Delete = function() {
    var scope = gs.getProperty('x_880077_crossplan.scope_id');
    var category = new GlideRecord('x_880077_crossplan_crsdb_category');
    category.addQuery('status', 'Created');
    category.query();

    while (category.next()) {
        var cat = new GlideRecord('sc_category');
        cat.addQuery('sys_scope', scope);
        cat.addQuery('sys_id', category.id);
        cat.query();
        while (cat.next()) {
            var gr = new GlideRecord('x_880077_crossplan_crs_jobqueue');
            gr.initialize();
            gr.state = "Ready";
            gr.body = cat.getUniqueValue();
            gr.topic = "Delete Category";
            gr.retry = 0;
            gr.insert();
        }
    }
};

CRSDB_Provider.getIDByName = function getIDByName(name){
	var gr = new GlideRecord('x_880077_crossplan_crsdb_category');
	gr.addQuery("name",name);
	gr.query();
	if(gr.next()){	
		return gr.id;
	}
	return -1;
};
