var Provider_Title = "CRS_Provider";
var CatalogID = "e0d08b13c3330100c8b837659bba8fb4"; //Service


function create_provider_category(providerID){
	GCP_Api_Token = CRSDB.find("GCP_API_TOKEN");	
	var response = CRS_API.requestGet("https://35.222.73.205/apis/pkg.crossplane.io/v1/providers",GCP_Api_Token);
	response_Json = JSON.parse(response.getBody());
	providers = response_Json["items"];
	CRSDB_Category.deleteAll();
	for (count in providers){
		provider = providers[count];
		categoryID = Category_Script.createWithParent(provider["metadata"]["name"],provider["spec"]["package"],providerID);
		create_provider_catalogItem(provider["metadata"]["name"],providerID,categoryID,GCP_Api_Token);
	}
}

function create_provider_catalogItem(name,providerID,categoryID,GCP_Api_Token){
	var response = CRS_API.requestGet("https://35.222.73.205/apis/apiextensions.k8s.io/v1/customresourcedefinitions",GCP_Api_Token);
	response_json = JSON.parse(response.getBody());
	response_json_items = response_json["items"];	
	for (i in response_json_items){
		response_json_ownerreferances = response_json_items[i]["metadata"]["ownerReferences"];
		for (j in response_json_ownerreferances){
			response_json_ownerreferance = response_json_ownerreferances[j];
			if (name == response_json_ownerreferance["name"]){
				CRS_Log.debug("Res: " + JSON.stringify(response_json_items[i]["metadata"]["name"]));
				Catalog_Item_Script.create(response_json_items[i]["metadata"]["name"],"description", CatalogID, categoryID, "25");
			}

		}
	}
}



(function() {	
	//Category_Script.InitCategory();
	CRSDB_CatItem.deleteAll();
	var providerID = CRSDB.find(Provider_Title);
	create_provider_category(providerID);
	//var azureCategoryID = CRSDB.find("AWS").id;		
	//Catalog_Item_Script.create("TESTCatalogItem010","description_CRS", CatalogID, azureCategoryID, "25");	


})();
